package com.forezp.feign.eureka;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@FeignClient(value = "service-hi",fallback = EurekaInvokerFeed.class)public interface EurekaInvoker {    @RequestMapping(value = "/hello",method = RequestMethod.GET)    public String getHello();}