package com.forezp.feign.eureka;import feign.Param;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;@FeignClient(value = "service-tendece",fallback = TendenceServiceFeed.class)public interface TendenceService {    @RequestMapping(value = "/service" , method = RequestMethod.GET)    public String getName(@RequestParam String name);}