package com.forezp.serviceribbon.Controller;import com.forezp.serviceribbon.EurekaService.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class EurekaController {    @Autowired    private UserService userService;    @RequestMapping("/ribbon")    public String getRibbon(String name){        return userService.hiService(name);    }}