package com.forezp.serviceribbon.EurekaService;import com.netflix.hystrix.contrib.javanica.annotation.HystrixCommand;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.web.client.RestTemplate;@Servicepublic class UserService {    @Autowired    RestTemplate restTemplate;    @HystrixCommand(fallbackMethod = "feedback")    public String hiService(String name) {        return restTemplate.getForObject("http://SERVICE-TENDECE/service?name="+name,String.class);    }    public String feedback(String name){        return name + "has some error!";    }}